import React, { Component } from 'react'
import axios from 'axios'
export default class CommentList extends Component {

    constructor(props) {
        super(props)

        this.state = {
             comment:[]
        }
    }

    componentDidMount(){
        axios.get('https://jsonplaceholder.typicode.com/comments')
        .then(response =>{
            console.log(response)
            this.setState({
                comment:response
            })
        })
        .catch(error => {
            console.log(error)
        });
    }

    render() {
        return (
            <div>
                <label>Comment Lists From API</label>
            </div>
        )
    }
}
